import React from "react";
import { useState } from "react";
import { watchlist } from '../data/data';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { Tooltip,Grow } from "@mui/material";

const WatchList = () => {
  return (
    <div className="watchlist-container">
      <div className="search-container">
        <input
          type="text"
          name="search"
          id="search"
          placeholder="Search eg:infy, bse, nifty fut weekly, gold mcx"
          className="search"
        />
        <span className="counts"> {watchlist.length} / 50</span>
      </div>
      <ul className="list">
        {watchlist.map((stock, index) => {
          return (
            <WatchListItem stock={stock} key={index} />
          )
        })}
      </ul>
    </div>
  );
};

export default WatchList;

const WatchListItem = ({ stock }) => {
  const [showWatchListActions, setShowWatchListActions] = useState(false);

  const handleMouseEnter = (e) => {
    setShowWatchListActions(true);
  };

  const handleMouseLeave = (e) => {
    setShowWatchListActions(false);
  };

  return (
    <li className="item" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <p className={stock.isDown ? "down" : "up"}>{stock.name}</p>
      <div className="item-info">
        <span>{stock.percent}</span>
        <span className={stock.isDown ? "down" : "up"}>{stock.isDown ? <KeyboardArrowDownIcon /> : <KeyboardArrowUpIcon />}</span>
        <span className={stock.isDown ? "down" : "up"}>{stock.price}</span>
      </div>
      {showWatchListActions && <watchListActions uid={stock.name}/>}
    </li>
  );
};

const watchListActions = ({ uid }) => {
  return (
    <div className="actions">
      <span>
        <Tooltip 
        title="Buy B" 

        TransitionComponent={Grow} arrow>
          <i className="fa fa-times"></i>
        </Tooltip>
      </span>
    </div>
  );
};
